using System;
using UnityEngine;

namespace MinishootRandomizer;

public class ArchipelagoSpriteProvider : ISpriteProvider
{
    public static string ArchipelagoBase64 = "";

    private readonly ISpriteProvider _innerProvider;

    public ArchipelagoSpriteProvider(ISpriteProvider innerProvider)
    {
        _innerProvider = innerProvider;
    }

    public SpriteData GetSprite(string identifier)
    {
        if (identifier == "Archipelago")
        {
            Texture2D texture = new Texture2D(128, 128, TextureFormat.DXT1, false);
            ImageConversion.LoadImage(texture, Convert.FromBase64String(ArchipelagoBase64));

            Sprite sprite = Sprite.CreateSprite(
                texture,
                new Rect(0, 0, 128f, 128f),
                new Vector2(0.5f, 0.5f),
                100f,
                0,
                SpriteMeshType.FullRect,
                Vector4.zero,
                false
            );

            return new SpriteData(sprite, 0.7f);
        }

        return _innerProvider.GetSprite(identifier);
    }
}
